# A string, s , of length n where s = C0C1...........Cn-1.
# An integer k, , where k is a factor of n.


def merge_the_tools(string, k):
    # your code goes here

    # to find the number of substring we can split from string 
    t = round(len(string)/k)
    print(t)

    # to iterate through the string and split the substrings
    for i in range(t):
        inte = k*(i+1)
        # print(string[i*k:inte])
        sub = string[i*k:inte]

        # to remove the duplicate character from the substring
        st =""
        for c in sub:
            if c not in st:
                st+=c

        print(st)

        
merge_the_tools('',3452)